import React from 'react';
import { View, Text, TextInput, Button, StyleSheet } from 'react-native';

const App = () => {
  const [artistName, setArtistName] = React.useState('');
  const [artistId, setArtistId] = React.useState('');
  const [contractorName, setContractorName] = React.useState('');
  const [cache, setCache] = React.useState('');
  const [eventDate, setEventDate] = React.useState('');
  const [address, setAddress] = React.useState('');

  const submitContract = () => {
    const formData = {
      artistName,
      artistId,
      contractorName,
      cache,
      eventDate,
      address
    };

    fetch('http://localhost:3000/submit-contract', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
    .then(response => response.json())
    .then(data => {
      if (data.success) {
        alert('Contrato enviado com sucesso!');
      } else {
        alert('Ocorreu um erro ao enviar o contrato. Por favor, tente novamente.');
      }
    })
    .catch(error => {
      console.error('Erro ao enviar contrato:', error);
      alert('Ocorreu um erro ao enviar o contrato. Por favor, tente novamente.');
    });
  };

  return (
    <View style={styles.container}>
      <TextInput
        style={styles.input}
        placeholder="Nome do Artista"
        value={artistName}
        onChangeText={setArtistName}
      />
      <TextInput
        style={styles.input}
        placeholder="ID do Artista"
        value={artistId}
        onChangeText={setArtistId}
      />
      <TextInput
        style={styles.input}
        placeholder="Nome do Contratante"
        value={contractorName}
        onChangeText={setContractorName}
      />
      <TextInput
        style={styles.input}
        placeholder="Cachê"
        value={cache}
        onChangeText={setCache}
      />
      <TextInput
        style={styles.input}
        placeholder="Data do Evento"
        value={eventDate}
        onChangeText={setEventDate}
      />
      <TextInput
        style={styles.input}
        placeholder="Endereço"
        value={address}
        onChangeText={setAddress}
      />
      <Button title="Enviar Contrato" onPress={submitContract} />
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
  },
  input: {
    width: '100%',
    height: 40,
    marginBottom: 10,
    paddingHorizontal: 10,
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
  },
});

export default App;
